import { ChangeDetectionStrategy, ChangeDetectorRef, Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../core/auth.service';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class LoginComponent {
  email = '';
  password = '';
  loading = false;

  constructor(
    private readonly router: Router,
    private readonly auth: AuthService,
    private readonly cdr: ChangeDetectorRef
  ) {}

  submit(): void {
    if (!this.email || !this.password) {
      return;
    }

    this.loading = true;
    this.cdr.markForCheck();
    setTimeout(() => {
      this.auth.login();
      this.loading = false;
      this.router.navigateByUrl('/dashboard');
    }, 600);
  }
}

<div class="login-page">
  <div class="login-card card">
    <div class="login-card__header">
      <div class="brand-mark">L</div>
      <div>
        <div class="title">Lenegewa Case Management</div>
        <div class="subtitle">Sign in to continue</div>
      </div>
    </div>

    <form (ngSubmit)="submit()" autocomplete="off">
      <div class="form-field">
        <label>Email</label>
        <input type="email" [(ngModel)]="email" name="email" placeholder="you@example.org" required />
      </div>

      <div class="form-field">
        <label>Password</label>
        <input type="password" [(ngModel)]="password" name="password" placeholder="Enter password" required />
      </div>

      <button class="primary-btn login-btn" [disabled]="loading" type="submit">
        {{ loading ? 'Signing in…' : 'Sign in' }}
      </button>
    </form>
  </div>
</div>
