import {
  AfterViewInit,
  Component,
  ElementRef,
  ViewChild,
} from '@angular/core';
import { CommonModule, Location } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register-client',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './register-client.component.html',
  styleUrls: ['./register-client.component.scss'],
})
export class RegisterClientComponent implements AfterViewInit {
  @ViewChild('agreementCanvas') agreementCanvas!: ElementRef<HTMLCanvasElement>;
  @ViewChild('consentCanvas') consentCanvas!: ElementRef<HTMLCanvasElement>;

  step: 1 | 2 = 1;
  clientName = '';

  // Agreement canvas state
  private agCtx!: CanvasRenderingContext2D;
  private agDrawing = false;
  hasSig = false;

  // Consent canvas state
  private csCtx!: CanvasRenderingContext2D;
  private csDrawing = false;
  hasConsentSig = false;

  constructor(
    private readonly location: Location,
    private readonly router: Router
  ) {}

  ngAfterViewInit(): void {
    this.initCanvas(this.agreementCanvas, (ctx) => (this.agCtx = ctx));
  }

  // ── Agreement ──────────────────────────────────────────────────────────────

  agStart(e: MouseEvent): void {
    this.agDrawing = true;
    this.agCtx.beginPath();
    this.agCtx.moveTo(e.offsetX, e.offsetY);
  }

  agDraw(e: MouseEvent): void {
    if (!this.agDrawing) return;
    this.hasSig = true;
    this.agCtx.lineTo(e.offsetX, e.offsetY);
    this.agCtx.stroke();
  }

  agStop(): void {
    this.agDrawing = false;
  }

  agClear(): void {
    const c = this.agreementCanvas.nativeElement;
    this.agCtx.clearRect(0, 0, c.width, c.height);
    this.hasSig = false;
  }

  confirmAgreement(): void {
    if (!this.clientName.trim() || !this.hasSig) return;
    this.step = 2;
    setTimeout(() => {
      this.initCanvas(this.consentCanvas, (ctx) => (this.csCtx = ctx));
    }, 0);
  }

  // ── Data Consent ───────────────────────────────────────────────────────────

  csStart(e: MouseEvent): void {
    this.csDrawing = true;
    this.csCtx.beginPath();
    this.csCtx.moveTo(e.offsetX, e.offsetY);
  }

  csDraw(e: MouseEvent): void {
    if (!this.csDrawing) return;
    this.hasConsentSig = true;
    this.csCtx.lineTo(e.offsetX, e.offsetY);
    this.csCtx.stroke();
  }

  csStop(): void {
    this.csDrawing = false;
  }

  csClear(): void {
    const c = this.consentCanvas.nativeElement;
    this.csCtx.clearRect(0, 0, c.width, c.height);
    this.hasConsentSig = false;
  }

  confirmConsent(): void {
    if (!this.hasConsentSig) return;
    this.router.navigate(['/clients', 'new', 'intake']);
  }

  // ── Shared ─────────────────────────────────────────────────────────────────

  goBack(): void {
    if (this.step === 2) {
      this.step = 1;
    } else {
      this.location.back();
    }
  }

  private initCanvas(
    ref: ElementRef<HTMLCanvasElement>,
    assign: (ctx: CanvasRenderingContext2D) => void
  ): void {
    const canvas = ref.nativeElement;
    canvas.width = canvas.offsetWidth;
    canvas.height = canvas.offsetHeight;
    const ctx = canvas.getContext('2d')!;
    ctx.strokeStyle = '#1e293b';
    ctx.lineWidth = 2;
    ctx.lineCap = 'round';
    ctx.lineJoin = 'round';
    assign(ctx);
  }
}
<div class="page-header">
  <button class="back-btn" (click)="goBack()">
    <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
      <polyline points="15 18 9 12 15 6"/>
    </svg>
  </button>
  <div>
    <h1>Register New Client</h1>
    <p class="subtitle">Complete the agreement and consent forms</p>
  </div>
</div>

<div class="step-indicator">
  <div class="step-dot" [class.active]="step === 1" [class.done]="step > 1">
    <span *ngIf="step <= 1">1</span>
    <svg *ngIf="step > 1" width="11" height="11" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="3">
      <polyline points="20 6 9 17 4 12"/>
    </svg>
  </div>
  <div class="step-line" [class.done]="step > 1"></div>
  <div class="step-dot" [class.active]="step === 2">
    <span>2</span>
  </div>
  <div class="step-labels">
    <span [class.active]="step === 1" [class.done]="step > 1">Client Agreement</span>
    <span [class.active]="step === 2">Data Consent</span>
  </div>
</div>

<!-- ── Step 1: Client Agreement ──────────────────────────────────────────── -->
<section class="card agreement-card" *ngIf="step === 1">
  <h2>Client Agreement</h2>
  <p class="form-desc">
    Read the following agreement to the client. The client must provide their
    signature to confirm understanding and acceptance of the centre's rules and
    commitment to the rehabilitation program.
  </p>

  <div class="form-field">
    <label>Client Full Name *</label>
    <input
      type="text"
      [(ngModel)]="clientName"
      name="clientName"
      placeholder="Enter client's full name"
    />
  </div>

  <div class="terms-box">
    <div class="terms-label">Agreement Terms:</div>
    <p class="terms-text">
      I, the undersigned, agree to abide by the rules and regulations of the
      Lenegewa Rehabilitation Center. I commit to participating actively in all
      assigned programs, attending counseling sessions, and respecting the
      community guidelines. I understand that violations may result in review of
      my participation status.
    </p>
  </div>

  <div class="form-field sig-field">
    <label>Client Agreement Signature *</label>
    <div class="sig-canvas-wrap">
      <canvas
        #agreementCanvas
        class="sig-canvas"
        (mousedown)="agStart($event)"
        (mousemove)="agDraw($event)"
        (mouseup)="agStop()"
        (mouseleave)="agStop()"
      ></canvas>
    </div>
  </div>

  <div class="sig-actions">
    <button class="secondary-btn" type="button" (click)="agClear()">Clear</button>
    <button
      class="primary-btn"
      type="button"
      [disabled]="!clientName.trim() || !hasSig"
      (click)="confirmAgreement()"
    >
      Confirm Signature
    </button>
  </div>
</section>

<!-- ── Step 2: Data Consent ──────────────────────────────────────────────── -->
<section class="card agreement-card" *ngIf="step === 2">
  <h2>Data Consent</h2>
  <p class="form-desc">
    Inform the client of how their personal data will be collected, stored, and
    used. The client must sign below to confirm their consent to data processing
    in accordance with the programme's privacy policy.
  </p>

  <div class="terms-box">
    <div class="terms-label">Data Use Terms:</div>
    <p class="terms-text">
      I consent to the Lenegewa Rehabilitation Center collecting and storing my
      personal information, including health and case management records, for the
      purpose of providing rehabilitation services and programme reporting. I
      understand that my data will be kept confidential and will not be shared
      with third parties without my explicit consent, except where required by
      law. I have the right to request access to, correction of, or deletion of
      my personal data at any time.
    </p>
  </div>

  <div class="form-field sig-field">
    <label>Data Consent Signature *</label>
    <div class="sig-canvas-wrap">
      <canvas
        #consentCanvas
        class="sig-canvas"
        (mousedown)="csStart($event)"
        (mousemove)="csDraw($event)"
        (mouseup)="csStop()"
        (mouseleave)="csStop()"
      ></canvas>
    </div>
  </div>

  <div class="sig-actions">
    <button class="secondary-btn" type="button" (click)="csClear()">Clear</button>
    <button
      class="primary-btn"
      type="button"
      [disabled]="!hasConsentSig"
      (click)="confirmConsent()"
    >
      Confirm &amp; Continue
    </button>
  </div>
</section>
